mapper.insert:
INSERT INTO TB_GROUP(
	GROUP_NAME
	,PARENT_SEQ
) VALUES(
	'new Node'
	,#{parent_id}
)

	SELECT LAST_INSERT_ID()

mapper.update:
UPDATE TB_GROUP SET
	GROUP_NAME = #{new_text}
WHERE
	SEQ = #{seq}
	AND GROUP_NAME = #{old_text}

UPDATE TB_GROUP SET
	PARENT_SEQ = #{new_parent}
WHERE
	SEQ = #{seq}
	AND PARENT_SEQ = #{old_parent}
